const useStyles = makeStyles({
  root: {
    marginTop: '15%'
  },
});

const SignUp = () =>  {
  const classes = useStyles();

	const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault()
    const formData = Object.fromEntries(new FormData(e.target));
    console.log(formData)

  }
        return (


          <Container maxWidth="sm"   className={classes.root}>
            <form onSubmit={handleSubmit}>
            <Paper component="span" m={10} container spacing={4} >
                <div container>
                    <Grid container spacing={8} alignItems="flex-end">
                        <Grid item>
                            <Face />
                        </Grid>
                        <Grid item md={true} sm={true} xs={true}>
                            <TextField id="userName" label="Username" name="userName" type="text" fullWidth autoFocus required />
                        </Grid>
                    </Grid>
                    <Grid container spacing={8} alignItems="flex-end">
                        <Grid item>
                            <AlternateEmailIcon />
                        </Grid>
                        <Grid item md={true} sm={true} xs={true}>
                            <TextField id="userEmail" label="Email" name="userEmail" type="email" fullWidth autoFocus required />
                        </Grid>
                    </Grid>
                    <Grid container spacing={8} alignItems="flex-end">
                        <Grid item>
                            <Fingerprint />
                        </Grid>
                        <Grid item md={true} sm={true} xs={true}>
                            <TextField id="userPassword" label="Password" name="userPassword" type="password" fullWidth required />
                        </Grid>
                    </Grid>
                    <Grid container style={{margin: "20px"}} alignItems="center" justify="space-between">
                        <Grid item>
                            <FormControlLabel control={
                                <Checkbox
                                    color="primary"
                                />
                            } label="Remember me" />
                        </Grid>
                        <Grid item>
                            <Button disableFocusRipple disableRipple style={{ textTransform: "none" }} variant="text" color="primary">Forgot password ?</Button>
                        </Grid>
                    </Grid>
                    <Grid container justify="center" style={{ marginTop: '10px' }}>
                        <Button type="submit" variant="outlined" color="primary" style={{  textTransform: "none" , width: "50%"}}>Login</Button>
                    </Grid>
                </div>
            </Paper>
            </form>
            </Container>
        );
    }


    export default SignUp
