import SSH2Shell from 'ssh2shell'


export const sshShell = (config, cb) => {
  var host = {
   server: {
     host: config.host,
     port:config.port,
     userName: config.user,
     password: config.password,
     privateKey:config.privateKey
   },
   commands:config.commands
  }
  var SSH = new SSH2Shell(host)
  SSH.connect((output) => {
      cb(output)
  })
}
